docker compose exec -T database-1431 mysql -u hamichlol_user -p hamichlol_main -e "SELECT p.page_id, p.page_title, p.page_latest, MAX(r.rev_timestamp) latest_ts FROM mw_page p LEFT JOIN mw_templatelinks tl ON tl.tl_from = p.page_id AND tl.tl_target_id = 18 LEFT JOIN mw_categorylinks cl1 ON cl1.cl_from = p.page_id AND cl1.cl_to = 'ערכים_לפתיחה' LEFT JOIN mw_categorylinks cl2 ON cl2.cl_from = p.page_id AND cl2.cl_to = 'המכלול:_ערכים_שנוצרו_במכלול' LEFT JOIN mw_categorylinks cl3 ON cl3.cl_from = p.page_id AND cl3.cl_to = 'המכלול:_פירושונים_שנוצרו_במכלול' LEFT JOIN mw_categorylinks cl4 ON cl4.cl_from = p.page_id AND cl4.cl_to = 'המכלול:_ערכים שתורגמו_במכלול' LEFT JOIN mw_revision r ON r.rev_page = p.page_id LEFT JOIN mw_comment c ON r.rev_comment_id = c.comment_id WHERE p.page_namespace = 0 AND p.page_is_redirect = 0 AND tl.tl_from IS NULL AND cl1.cl_from IS NULL AND cl2.cl_from IS NULL AND cl3.cl_from IS NULL AND cl4.cl_from IS NULL AND ( c.comment_text LIKE '%יובאה%' OR c.comment_text LIKE '%ע%דכון מוויקיפדיה%' OR c.comment_text LIKE '%יבוא מ%ויקיפדיה%' OR c.comment_text LIKE '%עדכון מויקיפדיה%' OR c.comment_text LIKE '%עידכון מויקיפדיה%' ) GROUP BY p.page_id, p.page_title, p.page_latest;" > not1.txt